//taken from https://github.com/cms-sw/cmssw/blob/CMSSW_9_0_X/SimCalorimetry/CaloSimAlgos/src/CaloCachedShapeIntegrator.cc
vector<double> makeHcalShape(vector<double> vin_){
  vector<double> v_(281,0.);
  for(int t = 0; t < 256; ++t) 
  {
    double amount = t < vin_.size() ? vin_[t] : 0.;
    for(int istep = 0; istep < 25; ++istep) 
    {
      int ibin = t + istep;
      v_[ibin] += amount;
    }
  }
  return v_;
}

